const express = require("express");
const cors = require("cors");
const fetch = require("node-fetch");

const app = express();
app.use(cors());  // ✅ CORS enabled for frontend requests
app.use(express.static('public'));  // ✅ Serve static files

const EBAY_AUTH_TOKEN = "v^1.1#i^1#f^0#I^3#p^1#r^0#t^"; // Replace with actual OAuth token

app.get("/ebay-listings", async (_, res) => {
    try {
        const response = await fetch(
            "https://api.ebay.com/buy/browse/v1/item_summary/search?q=laptop",
            {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${EBAY_AUTH_TOKEN}`,
                    "Content-Type": "application/json"
                }
            }
        );

        if (!response.ok) {
            throw new Error(`eBay API error: ${response.statusText}`);
        }

        const data = await response.json();
        res.json(data.itemSummaries || []);  // ✅ Ensure only relevant data is sent
    } catch (error) {
        console.error("Error fetching eBay listings:", error);
        res.status(500).json({ error: "Failed to fetch eBay listings" });
    }
});

app.get("/", (_, res) => {
    res.send("Welcome to the eBay Listings API");
});

app.get('/favicon.ico', (_, res) => {
    res.status(204).end();
});

// Start the server
const PORT = process.env.PORT || 4000;
app.listen(PORT, () => console.log(`Server running on port ${PORT}`));



